// 1.该文件专门用来创建整个应用的路由器
import Router from 'vue-router'
import Vue from 'vue'
Vue.use(Router) 
// 2.引入路由
import Container from "../view/front/Container"
import BackIndex from "../view/back/BackIndex"
import CityManage from "../view/back/RouterMain/CityManage.vue"
export default new Router ({
    mode: 'history',
    base: __dirname,
    routes: [
        {
            path: '*', redirect: '/'
        },
        {
            path: '/', component : Container
        },
        {
            path:'/backIndex',component : BackIndex,
            children:[
                {
                    path: '*', redirect: '/backIndex'
                },
                {
                    path: 'CityManage', component : CityManage
                }
            ]
        },
    ]
})
