// 1.该文件专门用来创建整个应用的路由器
import Router from 'vue-router'
import Vue from 'vue'
Vue.use(Router) 
import Home from "../components/Container"
// 2.引入路由

export default new Router ({
    mode: 'history',

    routes: [
        {
            path: '/', component: Home
        },

    ]
})
