// 1.该文件专门用来创建整个应用的路由器
import Router from 'vue-router'
import Vue from 'vue'
Vue.use(Router) 
// 2.引入路由
import Container from "../components/Container"

export default new VueRouter ({
    mode: 'history',
    base: __dirname,
    routes: [
        {
            path: '/home', component: Container
        },

    ]
})
