// 1.该文件专门用来创建整个应用的路由器
import Router from 'vue-router'
import Vue from 'vue'
Vue.use(Router) 
// 2.引入路由

const routers = [
    {
        path:'/',
        component : () => import('../components/Container.vue')
    },
    {
        path:'/back',
        component:()=>import('../view/back/backIndex.vue')
    }
]


// 3.创建一个路由器去管理路由
const router = new ({
    mode:'history',
    routers
})

export default router
