//在src下创建一个store文件夹，其中去创建index.js来创建Vuex中最为核心的store
import Vuex from "vuex"
//准备actions--用于响应组件中动作
const actions = {}
//准备mutations--用于操作数据（state）
const mutations = {}
//准备state--用于存储数据
const state = {}

//创建并暴露store
export default new Vuex.Store({
    actions,
    mutations,
    state,
})
