        this.tabIs = true;
        this.cityInfom = false;
      }
      console.log(newVal);
      for (let i = 0; i < newVal.length; i++) {
        //查询诗人的基本信息 name和introduction
        this.$http.get("peopleById", {
          params: {
            id: newVal[i]
          }
        }).then(response => {
          // console.log(response.data)
          this.peopleInformation.push(response.data); //通过键值对形式去存储数据
        }).catch(function (error) {
          console.log(error);
        });
        //查询诗人的基本信息 name和introduction
        this.$http.get("querySceneryById", {
          params: {
            id: newVal[i]
          }
        }).then(response => {
          // console.log(response.data)
          this.sceneryInfom.push(response.data); //通过键值对形式去存储数据
