// 1.该文件专门用来创建整个应用的路由器
import VueRouter from 'vue-router'
import Vue from 'vue'
Vue.use(VueRouter) 
// 2.引入路由

export default new Router({
    routes: [
      {
        path: '/login',
        component: Login
      },
      {
        path: '/main',
        component: Main
      }
    ]
  })
